import { render } from "@testing-library/react";
import React, { Component } from "react";
import WordForm from "./WordForm";
import Story from "./Story";
import MadLibsGen from "./MadLibsGen";

class MadLibs extends Component {
  state = {
    madlibsgen: MadLibsGen,
    selectedMadLib: MadLibsGen[0],
  };

  onWordInputChange = (key, value, index) => {
    const newState = {
      ...this.state,
    };
    newState.selectedMadLib.words[index] = {
      ...newState.selectedMadlib.words[index],
      value: value,
    };
    this.setState(newState);
  };

  render() {
    return (
      <div className="MadLibs">
        <h1>Mad Libs page</h1>
        <WordForm
          words={this.state.selectedMadLib.words}
          onInputChange={this.onWordInputChange}
        />
        <Story text={this.state.selectedMadLib.getText()} />
      </div>
    );
  }
}

