import React from "react";
import Container from "react-bootstrap/Container";
import OmdbContainer from "./OmdbContainer";
import { useQuery, gql } from "@apollo/client";
import { useState } from "react";

// const Something = () => {

// const GET_POKEMONS = gql`
// query Query_root($limit: Int) {
//   pokemon_v2_pokemon(limit: $limit) {
//     id
//     name
//   }
// }`

const GET_POKEMONS = gql`
  query Query_root($limit: Int) {
    pokemon_v2_pokemon(limit: $limit) {
      id
      name
    }
  }
`;

function usePokemonFilters() {
  const [filters, _updateFilter] = useState({
    id: undefined,
    name: undefined,
  });

  const updateFilter = (filterType, value) => {
    _updateFilter({
      [filterType]: value,
    });
  };

  return {
    models: { filters },
    operations: { updateFilter },
  };
}

function Something() {
  const { operations, models } = usePokemonFilters();
  const { data, loading, error, refetch } = useQuery(GET_POKEMONS);

  if (loading) return <div>Loading</div>;
  if (error) return <div>error</div>;

  return (
    <div>
      <Container>
        <div>Something Page</div>
        <OmdbContainer />
      </Container>

      <div className="Something">
        <h1>
          <a href="#pokemon_v2_pokemon"></a>Pokemons
        </h1>

        <div>
          <label>Search</label>
          <input
            onChange={(e) => operations.updateFilter("name", e.target.value)}
            type="string"
          />
        </div>

        <br />

        {data.pokemon_v2_pokemon.map((pokemon) => (
          <div>{JSON.stringify(pokemon)}</div>
        ))}

        <br />

        <button
          onClick={() =>
            refetch({
              pokemonsInput: { name: models.filters.name },
            })
          }
        >
          Submit!
        </button>
      </div>
    </div>
  );
}

export default Something;

