import React, { Component } from "react";

// render text inputs for all word objects in the array
// calls on InputChange() when change event occurs and pass in approp. arguments
class WordForm extends Component {
  onInputChange(key, index, event) {
    this.props.onInputChange(key, event.currentTarget.value, index);
  }

  render() {
    return (
      <div>
        <h1>This is the word form</h1>
      </div>
    );
  }
}

export default WordForm;
